MODULE async


enter
        DI

        PUSH AF
        LD   A,(runFlag)            ; get running flag
        CP   1                      ; is coroutine already active?
        JR   Z,.resume              ; yes, resume execution

        LD   A,1                    ; flag coroutine as running
        LD   (runFlag),A
        POP  AF

        ; Save main SP
        LD   (savedHL),HL           ; preserve HL''
        POP  HL                     ; HL'' = return address (after enter call)
        LD   (returnAddress),HL
        LD   HL,(savedHL)           ; restore HL''
        LD   (mainSP),SP

        LD   SP,coroutineStack      ; init coroutine SP
        LD   (savedHL),HL           ; preserve HL''
        LD   HL,(returnAddress)
        PUSH HL                     ; push return address on stack (after enter call)
        LD   HL,(savedHL)           ; restore HL''

        JR   .end                   ; resume execution after enter

.resume
        POP  AF

        ; Save main SP
        INC  SP
        INC  SP                     ; remove return address (after enter) from the stack
        LD   (mainSP),SP

        ; Restore coroutine registers
        LD   SP,coroutineContext-12
        POP  IY
        POP  IX
        POP  HL
        POP  DE
        POP  BC
        POP  AF

        LD   (savedHL),HL
        LD   SP,(coroutineSP)       ; restore coroutine SP
        LD   HL,(returnAddress)     ; get return address (after enter call)
        PUSH HL                     ; push return address on stack
        LD   HL,(savedHL)

.end
        EI
        RET                         ; resume execution after yield


yield
        DI

        ; Check if called between enter/exit statements
        PUSH HL
        LD   HL,runFlag             ; get running flag address
        BIT  0,(HL)                 ; is coroutine active?
        POP  HL
        JR   Z,.end                 ; no, end

        ; Save coroutine SP
        LD   (savedHL),HL
        POP  HL                     ; HL = return address (after yield call)
        LD   (returnAddress),HL
        LD   HL,(savedHL)
        LD   (coroutineSP),SP

        ; Save coroutine registers
        LD   SP,coroutineContext
        PUSH AF
        PUSH BC
        PUSH DE
        PUSH HL
        PUSH IX
        PUSH IY

        ; Restore main SP
        LD   SP,(mainSP)

.end
        EI
        RET                         ; resume execution


exit
        DI

        ; Flag coroutine as inactive
        LD   A,0
        LD   (runFlag),A

        ; Restore main SP
        LD   SP,(mainSP)

        EI
        RET


